import React, { useEffect, useState } from "react";
import logo from "./react.svg";
import axios from "axios";

import "./Home.css";

interface IRestaurant {
  name: string;
  Description: string;
  id: string;
}

class Home extends React.Component<{}, {}> {
  state = {
    restaurants: Array<IRestaurant>(),
    error: null,
  };

  componentDidMount = async () => {
    try {
      const response = await axios.get("http://localhost:1337/restaurants");
      // console.log(response.data[0].Description);
      this.setState({ restaurants: response.data });
    } catch (error) {
      this.setState({ error });
    }
  };
  public render() {
    const { error, restaurants } = this.state;
    return (
      <div className="flexbox-parent">
        <div className="flexbox-item header">Header</div>

        <div className="flexbox-item fill-area content flexbox-item-grow">
          <div className="fill-area-content flexbox-item-grow">
            Content
            <br />
            <br />
            Emulates height 100% with a horizontal flexbox with stretch
            <br />
            <br />
            This box with a border should fill the blue area except for the
            padding (just to show the middle flexbox item).
          </div>
        </div>

        <div className="flexbox-item footer">Footer</div>
      </div>
    );
  }
}

export default Home;
